INSERT INTO `graduation_course_target` VALUES (1, '使学生懂得如何从科学与工程问题中提炼系统的概念、反馈的概念，形成闭环思维', 1);
INSERT INTO `graduation_course_target` VALUES (2, '引导学生学习如何从机理或实验出发建立复杂工程问题或对象的数学模型', 1);
INSERT INTO `graduation_course_target` VALUES (3, '能够选择合适的软硬件工具，对工程问题进行实验设计与验证，并能够对实验结果和数据进行分析已获得有效结论', 2);
INSERT INTO `graduation_course_target` VALUES (4, '能够选择合适的软硬件工具，对工程问题进行实验设计与验证，并能够对实验结果和数据进行分析已获得有效结论', 3);
INSERT INTO `graduation_course_target` VALUES (5, '能够根据实验结果，进行口头或书面的总结，并具备向他人阐述实验结论的能力', 4);
INSERT INTO `graduation_course_target` VALUES (6, '1.掌握模拟电路的基本概念，掌握模拟电路常用元器件工作机理、特性参数和特点，掌握模拟电路基本单元电路的电路组成、工作原理、性能指标和特点。', 5);
INSERT INTO `graduation_course_target` VALUES (7, '具备模拟电路读图分析能力，能识别复杂电子系统中的模拟电路，分析其功能和原理，估算其性能指标。具备模拟电路选型设计的能力，能根据复杂电子系统功能要求选择合适的模拟电路，并设计电路参数。', 6);
INSERT INTO `graduation_course_target` VALUES (8, '具备模拟电路读图分析能力，能识别复杂电子系统中的模拟电路，分析其功能和原理，估算其性能指标。具备模拟电路选型设计的能力，能根据复杂电子系统功能要求选择合适的模拟电路，并设计电路参数。', 7);
INSERT INTO `graduation_point` VALUES (1, '1-1 能运用数学和自然科学对自动化领域复杂工程问题中的系统进行机理或实验建模', 1);
INSERT INTO `graduation_point` VALUES (2, '1-2 能运用数学和专业知识对复杂工程问题中的系统进行分析、设计和优化', 1);
INSERT INTO `graduation_point` VALUES (3, '4-1 能够结合研究方案，选用科学的实验方法，设计正确的实验步骤，构建实验系统或安全开展实验，正确采集、分析、处理实验数据，解释实验结果，并通过信息综合获得有效结论', 2);
INSERT INTO `graduation_point` VALUES (4, '4-2能够对不同影响因素或应用场景下的实验或仿真结果进行差异分析或合理解释，并与理论分析进行对比，获得合理有效的结论', 2);
