INSERT INTO `project` VALUES (12, 2, '蓝牙音箱', '蓝牙音箱是以蓝牙连接取代传统线材连接的音箱设备，在我们的生活中非常普遍。在种类众多的蓝牙音箱产品中，其本质是否相同呢？本案例我们将介绍蓝牙音箱的工作原理、设计方法等，揭秘蓝牙音箱的制作过程。\r\n蓝牙音箱是由许多功能不同的电路模块组成，其知识内容具有很强的实践性和工程性，蓝牙音箱的制作过程可以将理论与实际相结合，培养学生分析、设计及调试电子电路模块和系统的能力。\r\n蓝牙音箱的制作过程中，我们将任务分解为功能独立的若干子任务驱动学生积极性，从原理到设计将学习成果和专业知识贯穿于各个环节中，培养学生的工程素养和创新能力。\r\n', '', 'ed6936af20184fef8db2e1c972436115.jpg', '2023-07-12 16:00:00', '2023-07-21 16:00:00', '目标', 0000000000, 0000000000, 6, '2023-07-13 09:37:11', NULL, NULL, NULL, NULL, NULL);
