mapper.update:
UPDATE EMDV4_student_task_book
SET `status`= CASE id

    WHEN #{item.id} THEN #{item.status}

END
WHERE id IN

    #{item.id}

UPDATE EMDV4_student_task_book
SET `current_child`= CASE id

    WHEN #{item.id} THEN #{item.currentChild}

END
WHERE id IN

    #{item.id}

UPDATE EMDV4_student_task_book SET score=#{score},
                                   pass_status=#{passStatus}
                               WHERE id=#{id}

UPDATE EMDV4_student_task_book
SET `score`= CASE id

    WHEN #{item.id} THEN #{item.score}

END,
    pass_status=CASE id

    WHEN #{item.id} THEN #{item.passStatus}

END
WHERE id IN

    #{item.id}

mapper.select:
WITH RECURSIVE find_root AS ( SELECT * FROM EMDV4_student_task_book WHERE id = #{id} -- 您的节点ID
UNION ALL
SELECT t.* FROM EMDV4_student_task_book t INNER JOIN find_root fr ON t.id = fr.p_id ) SELECT * FROM find_root WHERE p_id IS NULL;

WITH RECURSIVE find_root AS ( SELECT tb.* FROM EMDV4_student_task_book tb LEFT JOIN EMDV4_component c ON c.block_id=tb.id WHERE c.id = #{componentId} -- 您的节点ID
UNION ALL
SELECT t.* FROM EMDV4_student_task_book t INNER JOIN find_root fr ON t.id = fr.p_id ) SELECT * FROM find_root WHERE level=2;

mapper.update:
UPDATE EMDV4_student_task_book
SET weighting=

        WHEN #{item.id} THEN #{item.weighting}

WHERE id IN

        #{item.id}

mapper.select:
SELECT * FROM EMDV4_student_task_book
WHERE id IN

    #{id}

mapper.update:
