mapper.update:
UPDATE proejct_student_group SET
                                 name=#{name},
                                 last_modified_user=#{lastModifiedUser},
                                 last_modified_time=#{lastModifiedTime}
WHERE id=#{id}

mapper.select:
SELECT * FROM project_group_student WHERE group_id=
                                          (SELECT project_group_student.group_id
                                           FROM project_group_student INNER JOIN proejct_student_group
                                               ON project_group_student.group_id=proejct_student_group.id
                                           WHERE project_group_student.student_id=#{studentId}
                                                and proejct_student_group.project_id=#{projectId});

SELECT Student.id, Student.student_id, Student.student_name
FROM Student INNER JOIN project_group_student ON Student.id=project_group_student.student_id
WHERE project_group_student.group_id=#{groupId};

SELECT pst.id FROM ((project_group_student gs INNER JOIN proejct_student_group pg ON gs.group_id=pg.id)
    INNER JOIN project_task_student pst ON pst.project_id=pg.project_id and pst.student_id=gs.student_id)
    INNER JOIN task ON pst.task_id=task.id WHERE pg.id=#{groupId} and task.num=#{taskNum};

SELECT Student.id, Student.student_id, Student.student_name, proejct_student_group.`name` as `group_name`, project_group_student.group_id
FROM (proejct_student_group INNER JOIN project_group_student ON proejct_student_group.id=project_group_student.group_id) INNER JOIN Student ON project_group_student.student_id=Student.id
WHERE proejct_student_group.project_id=#{projectId};
